    private void loginMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_loginMouseClicked
        boolean has_error = false;
        try{
            new Validator(librarianUserID.getText(), librarianPassword.getText());
        }catch (InvalidInput e){
            has_error = true;
            JOptionPane.showMessageDialog(null, e.getMessage());
        }
        try {
            new Authenticator(librarianUserID.getText(), librarianPassword.getText(), 1);
        }
        catch (InvalidCredentials invalidCredentials) {
            has_error = true;
            JOptionPane.showMessageDialog(null, invalidCredentials.getMessage());
        }
        if(!has_error){
            setVisible(false);
            new LibrarianHomepage().setVisible(true);
        }

    }//GEN-LAST:event_loginMouseClicked


