    private void proceedActionPerformed(java.awt.event.ActionEvent evt){//GEN-FIRST:event_proceedActionPerformed
        // TODO add your handling code here:
        int option = JOptionPane.showConfirmDialog(this, "Do you want to proceed", "Are you sure?", JOptionPane.YES_NO_OPTION);
        if (option == JOptionPane.YES_OPTION) {
            {
                
                JOptionPane.showMessageDialog(null, "Payments successful");
            }
            setVisible(false);
            new LibrarySection().setVisible(true);
        }

        clicked_price = false;
    }//GEN-LAST:event_proceedActionPerformed

    private void priceButtonMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_priceButtonMouseClicked
        // TODO add your handling code here:

        
        double pricePerUnit = 950;
        double number_of_books = Integer.parseInt(bookNumber.getText());
        double total_price = (number_of_books*pricePerUnit) - (number_of_books*pricePerUnit*discount)/100;
        banner1.setText("Number of books: " + number_of_books);
        banner2.setText("Price per unit: 950/=");
        banner3.setText("After 15 percent discount Total price: "+ total_price);
        clicked_price = true;
        
        new FileIO(name.getText(), bookID.getText(), bookNumber.getText(), receipt.getText(), total_price);

    }//GEN-LAST:event_priceButtonMouseClicked

